import React, { useState } from 'react';
import AppLayout from '../components/AppLayout';
import Slider from 'react-slick';
import styled from 'styled-components';
import { FaArrowRight, FaArrowLeft } from 'react-icons/fa';

const images = [
  '/img/poster1.jpg',
  '/img/poster2.jpg',
  '/img/poster3.jpg',
  '/img/poster4.jpg',
  '/img/poster5.jpg',
];

const Mydiv = styled.div`
  width: 50%;
  margin: 10rem auto;
  height: 570px;
`;

const Home = () => {
  const NextArrow = ({ onClick }) => {
    return (
      <div className="arrow next" onClick={onClick}>
        <FaArrowRight />
      </div>
    );
  };

  const PrevArrow = ({ onClick }) => {
    return (
      <div className="arrow prev" onClick={onClick}>
        <FaArrowLeft />
      </div>
    );
  };
  const [imageIndex, setImageIndex] = useState(0);
  const settings = {
    isfinite: true,
    lazyLoad: true,
    speed: 300,
    sliderToShow: 3,
    centerMode: true,
    centerPadding: 0,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    befroeChange: (current, next) => setImageIndex(next),
  };
  return (
    <>
      <AppLayout>
        <Mydiv>
          <Slider {...settings}>
            {images.map((img, idx) => (
              <div
                className={idx === imageIndex ? 'slide activeSlide' : 'slide'}
              >
                <img src={img} alt={img} />
              </div>
            ))}
          </Slider>
        </Mydiv>
      </AppLayout>
    </>
  );
};

export default Home;
