import React, { useState } from 'react';
import AppLayout from '../components/AppLayout';
import Slider from 'react-slick';
import styled from 'styled-components';
import { FaArrowRight, FaArrowLeft } from 'react-icons/fa';

const images = [
  '/img/poster1.jpg',
  '/img/poster2.jpg',
  '/img/poster3.jpg',
  '/img/poster4.jpg',
  '/img/poster5.jpg',
];

const Mydiv = styled.div`
  width: 50%;
  margin: 10rem auto;
  height: 570px;
`;

const Slide = styled.div`
  transform: scale(0.2);
  transition: transfrom 300ms;
  opacity: 0.5;
  img {
    width: 20rem;
    margin: 0 auto;
  }
`;

const ActiveSlide = styled.div`
  transform: scale(1.1);
  opacity: 1;
`;

const Home = () => {
  const NextArrow = ({ onClick }) => {
    return (
      <div className="arrow next" onClick={onClick}>
        <FaArrowRight />
      </div>
    );
  };

  const PrevArrow = ({ onClick }) => {
    return (
      <div className="arrow prev" onClick={onClick}>
        <FaArrowLeft />
      </div>
    );
  };
  const [imageIndex, setImageIndex] = useState(0);
  const settings = {
    infinite: true,
    lazyLoad: true,
    speed: 300,
    slidesToShow: 3,
    centerMode: true,
    centerPadding: 0,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    befroeChange: (current, next) => setImageIndex(next),
  };
  return (
    <>
      <AppLayout>
        <Mydiv>
          <Slider {...settings}>
            {images.map((img, idx) => (
              <div
                className={idx === imageIndex ? 'slide ActiveSlide' : 'Slide'}
              >
                <img style={{ width: '30%' }} src={img} alt={img} />
              </div>
            ))}
          </Slider>
        </Mydiv>
      </AppLayout>
    </>
  );
};

export default Home;
