import React from 'react';
import styled from 'styled-components';

const Card = styled.div`
  width: 150px;
`;

const MoviePoster = ({ i }) => {
  return (
    <>
      <div style={{ width: '650px', border: '1px solid red' }}>
        <Card>
          <img src={'/img/poster' + (i + 1) + '.jpg'} width="100%" />
        </Card>
      </div>
    </>
  );
};

export default MoviePoster;
